cmd.arg:
set -x -v -e;
trap "chmod a+w /snap -R" ERR EXIT;
cp -a /config/.ssh /config/.gitconfig /root/.;
chown root:root /root/.ssh /root/.gitconfig -R;
cd /snap/${package.name};
bash -x ../update-launchpad.sh;
echo "'setup launchpad finished'";

set -x -v -e;
trap "chmod a+w /rpm -R" ERR EXIT;
cp -a /rpm/* "$HOME/rpmbuild/SOURCES";
rm -Rf "$HOME/rpmbuild/SOURCES/rpmbuild";
spectool -g -R "$SPEC_FILE";
dnf -y builddep "$SPEC_FILE";
rpmbuild -bs "$SPEC_FILE";
cp -a "$HOME/rpmbuild" "/rpm/.";
if ! copr-cli list-packages ${package.name}>/dev/null 2>&1;
then
    copr-cli create --chroot fedora-25-i386 --chroot fedora-25-x86_64 ${package.name};
fi;
copr-cli build ${package.name} $HOME/rpmbuild/SRPMS/*.src.rpm;
echo "rpmbuilder finished";

set -x -v -e;
mkdir "$HOME/debbuild";
cp -a /deb/* "$HOME/debbuild";
rm -Rf "$HOME/debbuild/debbuild";
find . -name "*.DS_Store" -type f -delete
mkdir "$HOME/data";
cp -a $HOME/debbuild/build.orig/build.orig/* "$HOME/data";
cd "$HOME/debbuild/build";
debuild -us -uc;
cp -a "$HOME/debbuild" "/deb/.";
chmod a+w /deb -R;
echo "debbuilder finished";
